INSERT INTO `Centro_Idiomas`.`Publicaciones` (`idPublicaciones`, `Titulo`, `Contenido`, `Imagen`, `Administradores_idAdministrador`) VALUES (2, 'Convocatoria Talento SEP', 'Talento de Educacion Media Superior Convioca:\r\nA todos los alumnos que deseen participar en el programa de BecaTalento de la SEP como tutores de alumnos de Educacion Media Superior.\r\nRequisitos:\r\nContar con un promedio minimo de 80.\r\nSer de 6,7,8,9 semestre.\r\nContar con la disponibilidad los dias sabado.\r\nInterezados presentarse en el depto. De desarrollo academico con la impresion de su kardex en el horario de 12 a6 pm con la maestra Maria Yepez.\r\nEvaluacion Docente ene- jun 2016.', 'image/publicaciones/20160525040015.jpg', 'CIIRGDRG');

